    History history = new History();
    

    private void Button_submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Button_submitActionPerformed
        int nilai;
        String predikat;
        
        if (!Textfield_input.getText().isEmpty()) {
            nilai = Integer.parseInt(Textfield_input.getText());
            if (nilai <= 100) {
                predikat = kon(nilai);
                
                Label_result.setText(predikat);
                history.dataReciver(nilai);
            } else {
                Label_result.setText("Nilai melebihi kriteria");
            }
        } else {
            Label_result.setText("Masukkan nilai");
        }
    }//GEN-LAST:event_Button_submitActionPerformed

    private void Button_tableActionPerformed(java.awt.event.ActionEvent evt) {
        if (history.isVisible()) {
            history.setVisible(false);
        } else {
            history.setVisible(true);
            history.KROS.setViewportView(history.VBOS);
        }
    }



