    public String kon(int nilai){
        String predikat;
        if (nilai >= 90) {
            predikat = "Amat Baik";
        } else if(nilai >= 81){
            predikat = "Baik";
        } else if(nilai >=71){
            predikat = "Cukup Baik";
        } else if(nilai >=61){
            predikat = "Cukup";
        } else {
            predikat = "Sangat Buruk";
         }
        return predikat;
    }

